package com.hu;import com.hu.operation.duty.service.DutyRecordService;import com.hu.operation.duty.service.MonthDutyService;import javax.annotation.Resource;import org.springframework.scheduling.annotation.Scheduled;import org.springframework.stereotype.Component;@Componentpublic class ScheduledTasks {    @Resource    MonthDutyService monthDutyService;    @Resource    DutyRecordService dutyRecordService;    @Scheduled(cron = "0 0 0 1 * ?")    public void monthDutyChange() {        this.monthDutyService.monthDutyChange();        this.dutyRecordService.createMonthRecord();    }    @Scheduled(cron = "0 0 0 ? * MON")    public void weekDutyChange() {        this.monthDutyService.weekDutyChange();        this.dutyRecordService.createWeekRecord();    }    @Scheduled(cron = "0 0 0 * * ?")    public void holidayDutyChange() {        this.dutyRecordService.createHolidayRecord();    }    @Scheduled(cron = "0 0 15 ? * FRI")    public void sendWeekDutyMail() {        this.monthDutyService.sendWeekDutyMail();    }    @Scheduled(cron = "0 0 8 1 * ?")    public void sendMonthDutyMail() {        this.monthDutyService.sendMonthDutyMail();    }}