package com.hu.knowledge.knowledgeStore.service.impl;import com.hu.knowledge.knowledgeStore.dao.KnowledgeDao;import com.hu.knowledge.knowledgeStore.domain.Knowledge;import com.hu.knowledge.knowledgeStore.service.KnowledgeService;import com.hu.sysManagement.common.utils.Query;import java.util.List;import java.util.Map;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;@Servicepublic class KnowledgeServiceImpl implements KnowledgeService {    @Autowired    private KnowledgeDao knowledgeDao;    @Override    public List<Map<String, Object>> list(Query query) {        return knowledgeDao.list(query);    }    @Override    public int count(Map<String, Object> query) {        return knowledgeDao.count(query);    }    @Override    public Knowledge selectById(Long klId) {        return (Knowledge) knowledgeDao.selectById(klId);    }    @Override    public int save(Knowledge knowledge) {        return knowledgeDao.insert(knowledge).intValue();    }    @Override    public int update(Knowledge knowledge) {        return knowledgeDao.updateById(knowledge).intValue();    }    @Override    public int deleteById(Long klId) {        return knowledgeDao.deleteById(klId).intValue();    }}