package com.hu.operation.operationManage.domain;import com.baomidou.mybatisplus.annotations.TableId;import com.baomidou.mybatisplus.annotations.TableName;import java.io.Serializable;import java.util.Date;import lombok.Data;import org.springframework.format.annotation.DateTimeFormat;@TableName("record")@Datapublic class Record implements Serializable {    private static final long serialVersionUID = 1L;    @TableId    private Long reId;    private Long userId;    private Long wasteTime;    @DateTimeFormat(pattern = "yyyy-MM-dd HH:mm:ss")    private Date updateTime;    private Integer responsiblePerson;    private Long proType;    private String proDescribe;    private Long keyProblem;    private Long proState;    private String message;    private Long knowledgeId;    private Long customerId;    private Long sysId;}