package com.hu.operation.operationManage.service.impl;import com.hu.operation.operationManage.dao.ReadingDao;import com.hu.operation.operationManage.domain.Reading;import com.hu.operation.operationManage.service.ReadingService;import java.util.Date;import java.util.List;import java.util.Map;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import org.springframework.transaction.annotation.Transactional;@Transactional@Servicepublic class ReadingServiceImpl implements ReadingService {    @Autowired    private ReadingDao readingDao;    @Override    public List<Map<String, Object>> list(Map<String, Object> map) {        return readingDao.list(map);    }    @Override    public int count(Map<String, Object> map) {        return readingDao.count(map);    }    @Override    public List<Map<String, Object>> userList(Map<String, Object> map) {        return readingDao.userList(map);    }    @Override    public int amount(Map<String, Object> map) {        return readingDao.amount(map);    }    @Override    public int save(Reading reading) {        reading.setWriteTime(new Date());        return readingDao.insert(reading).intValue();    }    @Override    public int update(Reading reading) {        reading.setUpdateTime(new Date());        return readingDao.updateById(reading).intValue();    }    @Override    public int remove(Long rId) {        return readingDao.deleteById(rId).intValue();    }}