package com.hu.sysManagement.common.domain;import com.baomidou.mybatisplus.annotations.TableField;import com.baomidou.mybatisplus.annotations.TableId;import com.baomidou.mybatisplus.annotations.TableName;import lombok.Data;import java.io.Serializable;import java.util.Arrays;import java.util.List;@Data@TableName("district")public class District        implements Serializable {    private static final long serialVersionUID = 1L;    @TableId    private Long distId;    private String distName;    private String distDes;    private Long principalId;    private String prefecture;    @TableField(exist = false)    private List<String> prefectures;    public List<String> getPrefectures() {        if (this.prefecture == null) return null;        String[] strs = this.prefecture.split(",");        return Arrays.asList(strs);    }}