package com.hu.sysManagement.common.service.impl;import com.hu.sysManagement.common.dao.DistrictDao;import com.hu.sysManagement.common.domain.District;import com.hu.sysManagement.common.service.DistrictService;import com.hu.sysManagement.common.utils.BeanMapConvertUtil;import com.hu.sysManagement.system.domain.User;import java.util.List;import java.util.Map;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import org.springframework.transaction.annotation.Transactional;@Transactional@Servicepublic class DistrictServiceImpl        implements DistrictService {    @Autowired    private DistrictDao districtDao;    BeanMapConvertUtil<District> convertUtil = new BeanMapConvertUtil(District.class);    @Override    public List<Map<String, Object>> list(Map<String, Object> query) {        return districtDao.list(query);    }    @Override    public List<District> DistrictList(Map<String, Object> params) {        return convertUtil.convert(districtDao.list(params));    }    @Override    public int count(Map<String, Object> query) {        return districtDao.count(query);    }    @Override    public int save(District district) {        return districtDao.insert(district).intValue();    }    @Override    public int update(District district) {        return districtDao.updateById(district).intValue();    }    @Override    public int remove(Long distId) {        return districtDao.deleteById(distId).intValue();    }    @Override    public List<User> principalList() {        return districtDao.principalList();    }}