package com.hu.sysManagement.common.utils;import org.apache.shiro.crypto.hash.SimpleHash;import org.apache.shiro.util.ByteSource;import org.apache.shiro.util.ByteSource.Util;public class MD5Utils {    private static final String SALT = "huyw";    private static final String ALGORITH_NAME = "md5";    private static final int HASH_ITERATIONS = 2;    public static String encrypt(String pswd) {        String newPassword = new SimpleHash("md5", pswd, Util.bytes("huyw"), 2).toHex();        return newPassword;    }    public static String encrypt(String username, String pswd) {        String newPassword = new SimpleHash("md5", pswd, Util.bytes(username + "huyw"),                2).toHex();        return newPassword;    }    public static void main(String[] args){        System.out.println(encrypt("admin","123456"));    }}