package com.hu.sysManagement.common.utils;import com.hu.sysManagement.common.config.Constant;import java.io.File;import java.io.UnsupportedEncodingException;import java.net.URLDecoder;public class PathUtil {    public static String getConfigPath() {        String path = PathUtil.class.getProtectionDomain().getCodeSource().getLocation().getFile();        try {            path = URLDecoder.decode(path, Constant.charset);            if (path.contains("!")) {                path = new File(path).getParentFile().getParentFile().getParentFile().getPath() + "/config/";                path = path.replace("file:", "");            } else {                path = path + "config/";            }        } catch (UnsupportedEncodingException e) {            e.printStackTrace();        }        return path;    }}