package com.hu.sysManagement.common.utils;import java.util.LinkedHashMap;import java.util.Map;public class Query extends LinkedHashMap<String, Object> {    private static final long serialVersionUID = 1L;    private int offset;    private int limit;    public Query(Map<String, Object> params) {        putAll(params);        if ((params.get("offset") != null) && (params.get("limit") != null)) {            this.offset = Integer.parseInt(params.get("offset").toString());            this.limit = Integer.parseInt(params.get("limit").toString());            put("offset", Integer.valueOf(this.offset));            put("page", Integer.valueOf(this.offset / this.limit + 1));            put("limit", Integer.valueOf(this.limit));        }    }    public int getOffset() {        return this.offset;    }    public void setOffset(int offset) {        put("offset", Integer.valueOf(offset));    }    public int getLimit() {        return this.limit;    }    public void setLimit(int limit) {        this.limit = limit;    }}