package com.hu.sysManagement.common.utils.xss;import java.io.FileNotFoundException;import java.io.IOException;import java.io.PrintStream;import org.jsoup.Jsoup;import org.jsoup.nodes.Document;import org.jsoup.nodes.Document.OutputSettings;import org.jsoup.safety.Whitelist;public class JsoupUtil {    private static final Whitelist whitelist = Whitelist.basicWithImages();    private static final OutputSettings outputSettings = new OutputSettings().prettyPrint(false);    static {        whitelist.addAttributes(":all", new String[]{"style"});    }    public static String clean(String content) {        return Jsoup.clean(content, "", whitelist, outputSettings);    }    public static void main(String[] args) throws FileNotFoundException, IOException {        String text = "<a href=\"http://www.baidu.com/a\" onclick=\"alert(1);\">sss</a><script>alert(0);</script>sss";        System.out.println(clean(text));    }}