package com.hu.sysManagement.system.domain;import com.baomidou.mybatisplus.annotations.TableField;import com.baomidou.mybatisplus.annotations.TableId;import com.baomidou.mybatisplus.annotations.TableName;import lombok.Data;import java.io.Serializable;import java.util.Date;import java.util.List;import org.springframework.format.annotation.DateTimeFormat;@Data@TableName("sys_user")public class User        implements Serializable {    private static final long serialVersionUID = 1L;    @TableId    private Long userId;    private String username;    private String name;    private String password;    private Long deptId;    @TableField(exist = false)    private String deptName;    private String email;    private String mobile;    private Integer status;    private Long userIdCreate;    private Date gmtCreate;    private Date gmtModified;    @TableField(exist = false)    private List<Long> roleIds;    private Long sex;    @DateTimeFormat(pattern = "yyyy-MM-dd")    private Date birth;    private Long picId;    private String liveAddress;    private String hobby;    private String province;    private String city;    private String district;    private Long homepage;    @TableField(exist = false)    private List<Long> dataPerms;}