package com.hu.sysManagement.system.service.impl;import com.hu.sysManagement.system.dao.LoginLogDao;import com.hu.sysManagement.system.domain.LoginLog;import com.hu.sysManagement.system.service.LoginLogService;import java.util.List;import java.util.Map;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;@Servicepublic class LoginLogServiceImpl        implements LoginLogService {    @Autowired    private LoginLogDao loginLogDao;    @Override    public void save(LoginLog loginLog) {        this.loginLogDao.insert(loginLog);    }    @Override    public LoginLog getByid(Long id) {        return (LoginLog) this.loginLogDao.selectById(id);    }    @Override    public void update(LoginLog loginLog) {        this.loginLogDao.updateById(loginLog);    }    @Override    public List<Map<String, Object>> list(Map query) {        return this.loginLogDao.list(query);    }    @Override    public int count(Map query) {        return this.loginLogDao.count(query);    }}